create table accounts(id INT NOT NULL AUTO_INCREMENT,name VARCHAR(100) NOT NULL,PRIMARY KEY ( id ));create table ssl_certificates(id INT NOT NULL AUTO_INCREMENT,hostname varchar(1000) NOT NULL,expiration_date DATE,region varchar(100) not null,account_id int not null,PRIMARY KEY (id),FOREIGN KEY (account_id) references accounts(id));create table subnets(id INT NOT NULL AUTO_INCREMENT,region_name varchar(100) not null,account_id int not null,vpc_name varchar(100),vpc_id varchar(100),subnet_name varchar(100),aws_subnet_id varchar(100),cidr_block varchar(100),ip_count int,UNIQUE (aws_subnet_id),PRIMARY KEY (id),FOREIGN KEY (account_id) references accounts (id));