package com.getgo.cloudtower.dao;import com.getgo.cloudtower.model.Account;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.sql2o.Connection;import java.util.List;/** * Created by amirnashat on 8/2/16. */public class AccountDAO extends DefaultDAO {	static final Logger logger = LoggerFactory.getLogger(AccountDAO.class);	public final static List<Account> getAccounts() {		String accountsSQL = "SELECT id , " +				                     "name " +				                     "FROM accounts";		List<Account> accounts = null;		try (Connection conn = connection.open()) {			accounts = conn.createQuery(accountsSQL).executeAndFetch(Account.class);		} catch (Exception e) {			logger.error("Issue with retrieving ssl certificates",e);		}		return accounts;	}	public final static Account getAccountWithAccountId(Integer id) {		String accountSQL = "SELECT name " +				                    "FROM accounts " +				                    "WHERE id = :id";		Account account = null;		try (Connection conn = connection.open())  {			account = conn.createQuery(accountSQL)					          .addParameter("id" , id)					          .executeAndFetchFirst(Account.class);		} catch (Exception e) {			logger.error ("Issue with retrieving account for id <"+id+">",e);		}		return account;	}	public final static Account getAccountWithAccountName(String name) {		String accountSQL = "SELECT id , " +				                    "name " +				                    "FROM accounts " +				                    "WHERE name = :name";		Account account = null;		try (Connection conn = connection.open())  {			account = conn.createQuery(accountSQL)					          .addParameter("name" , name)					          .executeAndFetchFirst(Account.class);		} catch (Exception e) {			logger.error ("Issue with retrieving account for id <" + name + ">",e);		}		return account;	}	}