package com.getgo.cloudtower.dao;import com.getgo.cloudtower.model.Account;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.sql2o.Connection;import java.util.List;/** * Created by amirnashat on 8/2/16. */public class AccountDAO extends DefaultDAO {	static final Logger logger = LoggerFactory.getLogger(AccountDAO.class);	public final static List<Account> getAccounts() {		String accountsSQL = "SELECT id,name from accounts";		List<Account> accounts = null;		try (Connection conn = connection.open()) {			accounts = conn.createQuery(accountsSQL).executeAndFetch(Account.class);		} catch (Exception e) {			logger.error("Issue with retrieving ssl certificates",e);		}		return accounts;	}	public final static Account getAccount(Integer id) {		String accountSQL = "SELECT name from accounts where id = :id";		Account account = null;		try (Connection conn = connection.open())  {			logger.info("Obtaining account id <"+id+">");			account = conn.createQuery(accountSQL)					          .addParameter("id",id)					          .executeAndFetchFirst(Account.class);			logger.info("Retrieved account object <"+account.toString()+">");		} catch (Exception e) {			logger.error ("Issue with retrieving account for id <"+id+">",e);		}		return account;	}}