package com.getgo.cloudtower.dao;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.sql2o.Sql2o;import java.io.*;import java.util.Properties;/** * Created by amirnashat on 8/2/16. */public abstract class DefaultDAO {			static final Logger logger = LoggerFactory.getLogger(DefaultDAO.class);		public static final Sql2o connection;	static {						String host = null;		String user = null;		String password = null;		String port = null;		String database = null;				try {						File configFile = new File("./config/config.properties");			FileReader reader = new FileReader(configFile);			Properties props = new Properties();			props.load(reader);						host = props.getProperty( "host" );			user = props.getProperty( "user" );			port = props.getProperty( "port" );			password = props.getProperty( "password" );			database = props.getProperty( "database" );						reader.close();					}  catch (FileNotFoundException fileException) {						StringWriter writer = new StringWriter();			PrintWriter printWriter = new PrintWriter( writer );			fileException.printStackTrace( printWriter );			printWriter.flush();						String stackTrace = writer.toString();			logger.error("Could not locate the file config.properties for Cloudtower");			logger.error(stackTrace);					}  catch (IOException ioException) {						StringWriter writer = new StringWriter();			PrintWriter printWriter = new PrintWriter( writer );			ioException.printStackTrace( printWriter );			printWriter.flush();						String stackTrace = writer.toString();			logger.error("I/O exception reading the config.properties for Cloudtower");			logger.error(stackTrace);					}  catch (Exception exception) {						StringWriter writer = new StringWriter();			PrintWriter printWriter = new PrintWriter( writer );			exception.printStackTrace( printWriter );			printWriter.flush();						String stackTrace = writer.toString();			logger.error(stackTrace);				}				connection = new Sql2o("jdbc:mysql://"+host+":"+port+"/"+database+"?useUnicode=true&useJDBCCompliantTimezoneShift=true&useLegacyDatetimeCode=false&serverTimezone=UTC",user,password);			}}