package com.getgo.cloudtower.model;import java.sql.Date;/** * Created by amirnashat on 7/28/16. */public class SSLCertificate {	private Integer id;	private String hostname;	private Date expirationDate;	private Integer accountId;	private String regionName;	@Override	public boolean equals(Object o) {		if (this == o) return true;		if (!(o instanceof SSLCertificate)) return false;		SSLCertificate that = (SSLCertificate) o;		if (getId() != null ? !getId().equals(that.getId()) : that.getId() != null) return false;		if (getHostname() != null ? !getHostname().equals(that.getHostname()) : that.getHostname() != null)			return false;		if (getExpirationDate() != null ? !getExpirationDate().equals(that.getExpirationDate()) : that.getExpirationDate() != null)			return false;		if (getAccountId() != null ? !getAccountId().equals(that.getAccountId()) : that.getAccountId() != null)			return false;		return getRegionName() != null ? getRegionName().equals(that.getRegionName()) : that.getRegionName() == null;	}	@Override	public int hashCode() {		int result = getId() != null ? getId().hashCode() : 0;		result = 31 * result + (getHostname() != null ? getHostname().hashCode() : 0);		result = 31 * result + (getExpirationDate() != null ? getExpirationDate().hashCode() : 0);		result = 31 * result + (getAccountId() != null ? getAccountId().hashCode() : 0);		result = 31 * result + (getRegionName() != null ? getRegionName().hashCode() : 0);		return result;	}	@Override	public String toString() {		return "SSLCertificate{" + "id=" + id + ", hostname='" + hostname + '\'' + ", expirationDate=" + expirationDate + ", accountId=" + accountId + ", regionName='" + regionName + '\'' + '}';	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getRegionName() {		return regionName;	}	public void setRegionName(String regionName) {		this.regionName = regionName;	}	public String getHostname() {		return hostname;	}	public void setHostname(String hostname) {		this.hostname = hostname;	}	public Date getExpirationDate() {		return expirationDate;	}	public void setExpirationDate(Date expirationDate) {		this.expirationDate = expirationDate;	}	public Integer getAccountId() {		return accountId;	}	public void setAccountId(Integer accountId) {		this.accountId = accountId;	}}