package com.getgo.cloudtower.schedule;import com.getgo.cloudtower.service.SSLCertificateService;import com.getgo.cloudtower.service.SubnetService;import com.getgo.cloudtower.service.UsageService;import org.quartz.Job;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * Created by amirnashat on 10/4/16. */public class ProcessScheduler implements Job {		private Logger logger = LoggerFactory.getLogger(ProcessScheduler.class);		public void execute( JobExecutionContext context)		throws JobExecutionException {				SubnetService subnetService = new SubnetService();		subnetService.process();				SSLCertificateService sslCertificateService = new SSLCertificateService();		sslCertificateService.process();				UsageService usageService = new UsageService();		usageService.process();			}}