package com.getgo.cloudtower.service;import com.getgo.cloudtower.dao.AccountDAO;import com.getgo.cloudtower.model.Account;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.util.List;/** * Created by amirnashat on 9/22/16. */public class AccountService implements AWSService {		final Logger logger = LoggerFactory.getLogger(AccountService.class);		public List<Account> getAccounts() {				return AccountDAO.getAccounts();	}		public Account getAccountWithAccountId(Integer id) {		return AccountDAO.getAccountWithAccountId( id );	}		public Account getAccountWithAccountName(String name) {		return AccountDAO.getAccountWithAccountName( name );	}		@Override	public void process() {		logger.info("Method < 'process' > is not implemented for class < 'AccountService' >");	}		@Override	public List retrieve () {		logger.info("Method < 'retrieve' > not implemented for class < 'AccountService' >");		return null;	}	}